<script>
import { timeline, animate } from 'motion'
import BtnPlay from './BtnPlay.vue'
let mainTimeline
let animation = []
export default {
  components: {
    BtnPlay,
  },
  data() {
    return {
      isAnimating: false,
    }
  },

  methods: {
    onPlay() {
      this.initAnimation()
    },
    initAnimation() {
      if (this.isAnimating) {
        animation.forEach(a => a && a.cancel())
        mainTimeline && mainTimeline.cancel()
        animation = []
        this.isAnimating = false
        return
      }
      this.isAnimating = true

      const sequence = []
      animation.push(
        animate(
          '#left-hook',
          { transform: 'translateY(-12px)' },
          { duration: 1.4, repeat: Infinity, direction: 'alternate' }
        )
      )

      animation.push(
        animate(
          '#man-hands',
          { transform: 'rotate(12deg)' },
          { duration: 0.7, repeat: Infinity, direction: 'alternate' }
        )
      )

      sequence.push(
        [
          '#moving-block-2',
          {
            transform: 'translate3d(14px, 74px, 0px) rotate(-4deg)',
          },
          { duration: 1 },
        ],
        [
          '#main-hook',
          {
            transform: 'translate3d(14px, 74px, 0px) rotate(-4deg)',
          },
          { duration: 1, at: '<' },
        ],
        [
          '#main-hook',
          {
            transform: 'translate3d(17px, 30px, 0px) rotate(-8deg)',
          },
          { duration: 1 },
        ],
        [
          '#moving-block-star',
          {
            opacity: 1,
            transform: 'rotate(90deg)',
          },
          { duration: 0.3, at: '<', easing: 'linear' },
        ],
        [
          '#moving-block-star',
          {
            opacity: 0,
            transform: 'rotate(180deg)',
          },
          { duration: 0.2, easing: 'linear' },
        ],

        [
          '#moving-block-1',
          {
            transform: 'translate3d(6px, 64px, 0px) rotate(3deg)',
          },
          { duration: 1 },
        ],
        [
          '#main-hook_2',
          {
            transform: 'translate3d(6px, 64px, 0px) rotate(3deg)',
          },
          { duration: 1, at: '<' },
        ],

        [
          '#main-hook_2',
          {
            transform: 'translate3d(-6px, 10px, 0px) rotate(6deg)',
          },
          { duration: 1 },
        ],

        [
          '#moving-block-2',
          {
            opacity: 0,
          },
          { duration: 0.3 },
        ],
        [
          '#moving-block-1',
          {
            opacity: 0,
          },
          { duration: 0.3, at: '<' },
        ]
      )

      mainTimeline = timeline(sequence)
    },
  },
}
</script>

<style scoped>
