let mainTimeline
export default {
  components: {
    BtnPlay,
  },

  data() {
    return {
      isAnimating: false,
    }
  },

  methods: {
    onPlay() {
      this.initAnimation()
    },
    initAnimation() {
      if (this.isAnimating) {
        mainTimeline && mainTimeline.cancel()
        this.isAnimating = false
        return
      }

      this.isAnimating = true
      const sequence = []
      const resetStyle = (el, style = ['opacity', 'transform']) => {
        const $el = document.querySelector(el)
        if ($el) {
          style.forEach(s => {
            $el.style[s] = null
          })
        }
      }

      resetStyle('#hand-left-card')
      resetStyle('#hand-right-phone')
      resetStyle('#screen-right', ['fill'])
      resetStyle('#screen-left', ['fill'])

      sequence.push([
        '#hand-left-card',
        { transform: 'rotate(21deg) translateX(25px) translateY(-14px)' },
        { duration: 1 },
      ])

      sequence.push([
        '#hand-right-phone',
        { transform: ' rotate(-9deg) translateX(-20px) translateY(10px)' },
        { duration: 1, at: '<' },
      ])

      sequence.push(['#screen-right', { fill: '#0f8ee8' }, { duration: 0.5 }])

      sequence.push(['#screen-left', { fill: '#2599ee' }, { duration: 0.5, at: '<' }])

      mainTimeline = timeline(sequence)
    },
  },
}
</script>

<style scoped>
#hand-left-card,
#hand-right-phone {
  transform-origin: center center;
  transform-box: fill-box;
}
</style>
